SET content = '{"nav":{"home":"Domov","aboutUs":"O nas","careers":"Kariera","insights":"Vpogledi","apply":"Prijava","viewPositions":"Poglej pozicije","joinTeam":"Pridruži se ekipi"},"home":{"hero":{"title":"Zgradi svojo kariero z Win Win","subtitle":"Sales Team","description":"Smo prodajno podjetje, usmerjeno k doseganju rezultatov, ki je specializirano za prodajo telekomnikacijskih rešitev za b2c in b2b preko prodaje na terenu, klicnega centra in preko direktnih promocij na strateških lokacijah z višjimi provizijami kot ostali ponudniki.","joinTeam":"Pridruži se ekipi","viewPositions":"Poglej odprte pozicije"},"about":{"title":"Kdo smo","description":"Win Win d.o.o. je ekipa izkušenih prodajnih vodij in strokovnjakov, ki se specializira za neposredno trženje in dolgoročne odnose s strankami. Delujemo z več lokacij po Sloveniji in gradimo okolje, kjer se nagrajujejo strokovnost, trud, disciplina in vztrajnost.","stats":{"deals":"Zaključenih poslov","locations":"Lokacije","team":"Članov ekipe","experience":"Let izkušenj"}},"services":{"title":"Naše storitve","description":"Celovite prodajne rešitve in podporni sistemi, ki prinašajo rezultate","b2cField":"NAŠE STORITVE","internalSupport":"Interne podporne storitve","marketFocus":"Tržna usmeritev","core":{"promotions":{"title":"B2C Terenska Prodaja","description":"Promocijske aktivnosti na licu mesta z neposrednim angažiranjem strank na prometnih trgovskih lokacijah po Sloveniji, podprte z vnaprej pripravljenim terenom ter ciljno usmerjenimi promocijami v trgovskih centrih, na sejmih in drugih aktualnih dogodkovnih lokacijah."},"callCenter":{"title":"Klicni Center","description":"Profesionalna odhodna telefonska prodaja za generiranje vročih marketing leadov, podprta s strukturiranimi prodajnimi skriptami, KPI-usmerjeno uspešnostjo in CRM-osnovanim upravljanjem strank."},"b2b":{"title":"B2B Profesionalna Prodaja","description":"Telekomunikacije in napredne IKT storitve, ki podjetjem omogočajo učinkovite in merljive B2B prodajne rešitve, zanesljive korporativne telekomunikacijske sisteme ter celovite profesionalne podporne in svetovalne storitve."}},"support":{"training":{"title":"Prodajno usposabljanje in coaching","description":"Mesečni programi, mentorstvo, simulacije vlog in delavnice za razvoj veščin."},"teamDevelopment":{"title":"Razvoj ekipe","description":"14-dnevno uvajanje, selekcijski programi, izboljšanje uspešnosti in načrtovanje karierne poti."},"leadership":{"title":"Vodenje in management","description":"Spremljanje KPI-jev, nadzor uspešnosti, optimizacija procesov in coaching za voditelje ekip."},"crm":{"title":"CRM in optimizacija procesov","description":"Sodobni CRM sistemi, optimizacija skriptov, upravljanje z zadevami in strukturirani dnevni cilji."}},"market":{"b2c":{"title":"B2C tržna specializacija","description":"Primarni fokus na stanovanjske stranke, gospodinjstva in posamezne končne uporabnike."},"promotional":{"title":"Promocijska Prodaja","description":"Direktne promocijske aktivnosti na strateških lokacijah z osebnim angažmanom strank ter krepitvijo prepoznavnosti blagovne znamke skozi ciljno usmerjene kampanje na dogodkih, sejmih in v trgovskih centrih."},"b2b":{"title":"B2B Prodaja","description":"Sekundarni fokus na poslovne telekomunikacijske rešitve in ponudbe povezane z IKT."}}},"benefits":{"title":"Zakaj izbrati Win Win","description":"Zagotavljamo vse, kar potrebujete za uspešno kariero v prodaji","items":{"training":{"title":"Mesečno usposabljanje in mentorstvo","description":"Nenehno coaching in razvoj veščin s strani izkušenih prodajnih strokovnjakov"},"career":{"title":"Jasne karierne poti","description":"Strukturiran napredek od prodajnega svetovalca do voditelja ekipe in naprej"},"earning":{"title":"Visok potencial zaslužka","description":"Konkurenčna osnovna plača plus provizijska struktura, odvisna od uspešnosti"},"tools":{"title":"Moderna orodja in skripti","description":"Dostop do preverjenih prodajnih skriptov, CRM sistemov in kvalificiranih potencialnih strank"},"locations":{"title":"Več lokacij","description":"Pisarne v Trzinu in Kranju z operacijami na terenu po vsej državi"},"performance":{"title":"Usmerjenost k rezultatom","description":"Jasni KPI-ji in transparentne metrike za spremljanje vašega uspeha"}}},"joinSection":{"title":"Pridruži se vodilni prodajni ekipi v Sloveniji","description":"Gradimo najbolj učinkovito in spoštovano prodajno organizacijo v Sloveniji. Naši člani ekipe uživajo v vodilnem industrijskem usposabljanju, konkurenčnih plačilih in kulturi, ki praznuje uspeh.","startJourney":"Začni svojo pot","learnMore":"Več o nas"},"partners":{"title":"Naši zaupanja vredni partnerji"},"cta":{"title":"Pripravljen začeti svojo prodajno kariero?","description":"Pridruži se ekipi predanih strokovnjakov, ki so zavezani odličnosti, rasti in rezultatom. Iščemo motivirane posameznike, pripravljene dvigniti svojo kariero na novo raven.","explore":"Razišči priložnosti","learnMore":"Več o nas"},"process":{"title":"Naš proces","description":"Preprosta, transparentna pot do pridružitve naši ekipi","steps":{"apply":{"title":"Prijava online","desc":"Oddaj svojo prijavo preko našega portala"},"interview":{"title":"Razgovor","desc":"Srečaj se z našo ekipo in razpravljaj o priložnostih"},"selection":{"title":"Selekcijski program","desc":"14-dnevno obdobje usposabljanja in evalvacije"},"start":{"title":"Začni delati","desc":"Podpiši pogodbo in začni svojo kariero"}},"startApplication":"Začni svojo prijavo"},"app":{"title":"Prijavi se na poti","description":"Spremljaj svojo prijavo, prejemaj takojšnje posodobitve in ostani povezan z našo mobilno aplikacijo","comingSoon":"Kmalu na voljo"},"faq":{"title":"Pogosto zastavljena vprašanja","description":"Vse, kar moraš vedeti o pridružitvi Win Win","stillQuestions":"Še vedno imaš vprašanja?","getInTouch":"Kontaktiraj nas","items":{"experience":{"q":"Ali potrebujem prejšnje prodajne izkušnje?","a":"Ne. Prejšnje prodajne izkušnje so prednost, vendar niso obvezne. Zagotavljamo strukturirano uvajanje, skripte in nenehni coaching. Najpomembnejši sta motivacija, disciplina in pripravljenost za učenje."},"type":{"q":"Kakšne vrste prodaje bom opravljal?","a":"Delal boš v B2C prodaji, bodisi terenski prodaji od vrat do vrat ali prodaji v klicnem centru. Prodajal boš telekomunikacijske storitve, vključno s pogodbami za internet, TV in mobilne storitve za končne stranke."},"employment":{"q":"Ali gre za redno zaposlitev ali delo preko avtorske pogodbe?","a":"Možni sta obe možnosti. Odvisno od tvojega profila in dogovora lahko delaš kot redno zaposlen ali kot samostojni podjetnik (s.p. / sodelovanje s podjetjem)."},"hours":{"q":"Kakšni so delovni časi?","a":"Delovni časi so strukturirani in jasno določeni. Urniki za teren in klicni center so organizirani tako, da podpirajo produktivnost in ravnovesje med delom in zasebnim življenjem. Specifični urniki se razpravljajo med razgovorom."},"payment":{"q":"Kako deluje plačilo?","a":"Prejmeš osnovno plačo ali urno postavko plus provizijo, odvisno od uspešnosti. Tvoj zaslužek je neposredno odvisen od tvojih rezultatov. Visoko uspešni posamezniki lahko dosežejo nadpovprečen mesečni dohodek."},"advancement":{"q":"Kako hitro lahko napredujem znotraj podjetja?","a":"Napredovanje v karieri je odvisno od uspešnosti. Uspešni prodajni svetovalci lahko napredujejo v višje prodajne svetovalce, voditelje prodajnih ekip ali prodajne managerje. Aktivno promovuramo od znotraj."},"training":{"q":"Ali zagotavljate usposabljanje in podporo?","a":"Da. Prejmeš strukturirano uvajanje, prodajne skripte in CRM orodja, mesečno coaching in mentorstvo ter redna prodajna usposabljanja in povratne informacije. Nikoli nisi prepuščen sam sebi."},"locations":{"q":"Kje so vaše pisarne?","a":"Delujemo iz pisarn v Trzinu in Kranju. Terenska prodaja pokriva vso Slovenijo."},"remote":{"q":"Ali je možno delo od doma?","a":"Za izkušene kandidate in vodstvene pozicije je lahko delo od doma ali hibridno delo možno. To se ocenjuje individualno."},"selection":{"q":"Kakšen je selekcijski proces?","a":"Proces vključuje: online prijavo, osebni razgovor in prodajno simulacijo, 14-dnevni selekcijski in uvajalni program ter podpis pogodbe in dolgoročno sodelovanje."},"profile":{"q":"Kakšne ljudi iščete?","a":"Iščemo ljudi, ki so po naravi konkurenčni, usmerjeni k ciljem, pozitivni in disciplinirani, motivirani za profesionalno in finančno rast ter pripravljeni prevzeti odgovornost za svoj uspeh."},"different":{"q":"Kaj naredi Win Win drugačen od drugih prodajnih podjetij?","a":"Združujemo jasno strukturo, pošteno komunikacijo, močno coaching kulturo, dolgoročne karierne priložnosti in rezultatno usmerjeno, a podporno okolje. Pri Win Win uspeh ni sreča — je sistem."}}},"blog":{"title":"Z našega bloga","subtitle":"Najnovejši vpogledi","description":"Prodajne strategije, karierni nasveti in zgodbe iz Win Win ekipe","viewAll":"Poglej vse članke","noArticles":"Članki še niso na voljo. Preverite kmalu!"}},"about":{"hero":{"title":"O Win Win","description":"Smo ekipa, ki živi prodajo. Win Win d.o.o. je bil zgrajen na prepričanju, da se lahko prodaja izvaja drugače — pošteno, etično in z dolgoročnim razmišljanjem."},"vision":{"title":"Naša vizija","description":"Postati najbolj učinkovito in spoštovano prodajno podjetje v Sloveniji, znano po naši integriteti, rezultatih in zavezanosti tako strankam kot članom ekipe."},"mission":{"title":"Naša misija","description":"Zagotavljati več vrednosti strankam, kot pričakujejo, skozi zaupanje, strukturo in vrhunsko prodajno strokovnost. Gradimo dolgoročne odnose, ki koristijo vsem vključenim."},"values":{"title":"Naše temeljne vrednote","description":"Ti principi vodijo vse, kar počnemo, in določajo, kdo smo kot podjetje","items":{"integrity":{"title":"Integriteta in poštenost","description":"Gradimo zaupanje skozi transparentno komunikacijo in etične poslovne prakse"},"reliability":{"title":"Zanesljivost","description":"Naše stranke in člani ekipe se lahko zanašajo na nas pri izpolnjevanju naših zavez"},"communication":{"title":"Profesionalna komunikacija","description":"Jasna, spoštljiva in učinkovita komunikacija v vsaki interakciji"},"performance":{"title":"Uspešnost in rast","description":"Nenehno izboljševanje in merljivi rezultati poganjajo naš uspeh"},"support":{"title":"Ekipna podpora","description":"Skupaj uspemo skozi sodelovanje in medsebojno podporo"},"excellence":{"title":"Odličnost","description":"Stremimo k najvišjim standardom v vsem, kar počnemo"}}},"culture":{"title":"Podjetniška kultura","description":"Zgradili smo okolje, kjer lahko nadarjeni strokovnjaki uspevajo","items":["Rezultatno usmerjen način razmišljanja z jasnimi KPI-ji","Ekipno sodelovanje in deljenje znanja","Nenehno učenje in razvoj veščin","Programi coachinga in mentorstva","Priznanje in nagrade za odličnost","Ravnovesje med delom in zasebnim življenjem ter fleksibilnost"]},"team":{"title":"Naša ekipa","description":"Delujemo z več lokacij po Sloveniji z rastočo ekipo predanih prodajnih strokovnjakov","stats":{"members":"Aktivnih članov ekipe","field":"Terenska prodaja","callCenter":"Podpora","locations":"Več lokacij"},"fieldText":"Teren","callCenterText":"Klicni center","locationsText":"Trzin, Kranj in teren"}},"jobs":{"title":"Odprte pozicije","description":"Pridruži se naši ekipi visoko uspešnih prodajnih strokovnjakov. Nudimo konkurenčno plačilo, celovito usposabljanje in jasno napredovanje v karieri.","viewDetails":"Poglej podrobnosti","applyNow":"Prijavi se zdaj","loading":"Nalaganje pozicij...","noJobs":"Trenutno ni odprtih pozicij. Preverite kmalu!","noPosition":"Ne vidiš prave pozicije?","noPositionDesc":"Vedno iščemo nadarjene prodajne strokovnjake. Pošlji nam svojo prijavo in te bomo imeli v mislih za prihodnje priložnosti.","submitGeneral":"Oddaj splošno prijavo","backToAll":"Nazaj na vse pozicije","fullTime":"Polni delovni čas","applyForPosition":"Prijavi se za to pozicijo","responsibilities":"Glavne odgovornosti","whatWeOff